import { createClient } from '@supabase/supabase-js';

// Get Supabase URL and anon key from environment variables
const supabaseUrl = import.meta.env.VITE_SUPABASE_URL || '';
const supabaseAnonKey = import.meta.env.VITE_SUPABASE_ANON_KEY || '';

// Validate that required environment variables are present
if (!supabaseUrl || !supabaseAnonKey) {
  console.error('Missing Supabase environment variables:', {
    url: supabaseUrl ? 'Found' : 'Missing VITE_SUPABASE_URL',
    key: supabaseAnonKey ? 'Found' : 'Missing VITE_SUPABASE_ANON_KEY'
  });
  
  if (import.meta.env.DEV) {
    console.warn('Running in development mode with missing Supabase credentials!');
  }
}

// Create the Supabase client
export const supabase = createClient(supabaseUrl, supabaseAnonKey, {
  auth: {
    persistSession: true,
    autoRefreshToken: true,
    detectSessionInUrl: true
  }
});

// Export the URL and key for debugging purposes
export { supabaseUrl, supabaseAnonKey };
